"""# **Data PreProcessing**"""

# Fungsi untuk preprocessing teks
def preprocess_text(text):
    # Ganti <PRODUCT_NAME> dengan token "produk"
    text = re.sub(r'<PRODUCT_NAME>', 'produk', text)

    # Hapus <PROVIDER_NAME>, <USER_MENTION>, dan <URL>
    text = re.sub(r'<PROVIDER_NAME>', '', text)
    text = re.sub(r'<USER_MENTION>', '', text)
    text = re.sub(r'<URL>', '', text)

    # Case folding: ubah ke lowercase
    text = text.lower()

    # Hapus URL (jika ada yang tidak tertangani oleh <URL>)
    text = re.sub(r'http\S+', '', text)

    # Hapus hashtag
    text = re.sub(r'#\w+', '', text)

    # Hapus angka
    text = re.sub(r'\d+', '', text)

    # Hapus tanda baca dan simbol
    text = re.sub(r'[^\w\s]', ' ', text)

    # Hapus karakter tunggal
    text = re.sub(r'\s+[a-zA-Z]\s+', ' ', text)

    # Hapus spasi berlebih
    text = re.sub(r'\s+', ' ', text)
    text = text.strip()

    # Tokenisasi
    tokens = word_tokenize(text)

    # Hapus stopwords
    tokens = [word for word in tokens if word not in stop_words_indonesia]

    # Stemming
    tokens = [stemmer.stem(word) for word in tokens]

    return ' '.join(tokens)

# Terapkan preprocessing pada kolom tweet
